<?phpif (!defined("_BASEDIR_"))    exit();session_start();include_model("Categoria", "Gara", "Ranking", "IscrittoIndividuale");class EliminagaraRankingCtrl{    private $gare;    private $array_eventi = NULL;    private $ranking = NULL;    public function __construct()    {        //$this->gare = Gara::getGarePassate();        $this->ranking = new Ranking();        $this->array_eventi = $this->ranking->getRankingEventi();        if (isset($_POST['processa']))        {            $id_gara = empty($_POST['gare']) ? NULL : $_POST['gare'];            if (!empty($id_gara))            {                $eliminato = $this->deleteGaraFromRanking_ctrl($id_gara);                                $ergebniseinzel_cancellato = $this->ranking->eliminaDBSwgare($id_gara, "ergebniseinzel_$id_gara");                $names_cancellato = $this->ranking->eliminaDBSwgare($id_gara, "names_$id_gara");                if ($eliminato)                {                    $path = $_SERVER["REQUEST_URI"];                    $this->redirect($path);                }            }        }    }    public function redirect($path)    {        print '<META HTTP-EQUIV="refresh" CONTENT="0; URL=' . $path . '">';    }    public function deleteGaraFromRanking_ctrl($id_gara)    {        return $this->ranking->deleteGaraFromRanking($id_gara);    }    public function getEventi()    {        return $this->array_eventi;    }    public function getNomeGara($id_gara)    {        return $this->ranking->getNomeGara($id_gara);    }    public function getGare()    {        return $this->gare;    }}