<?phpif (!defined("_BASEDIR_"))    exit();session_start();include_model("Categoria", "Gara", "Ranking", "IscrittoIndividuale", "Utente");class GareEsterneAddRisCtrl{    private $gare;    private $array_eventi = NULL;    private $ranking = NULL;    private $elenco_categorie = NULL;    public function __construct()    {               //$this->gare = Gara::getGarePassate();        $this->ranking = new Ranking();        $this->array_eventi = $this->ranking->getRankingEventiConvalidati();        //$riepilogo_individiuale =  new RiepilogoIndividualeCompleto(Utente::ORGANIZZATORE);        $this->elenco_categorie = Categoria::elenco();           }    public function allCategorie()    {        return $this->elenco_categorie;    }        public function redirect($path)    {        print '<META HTTP-EQUIV="refresh" CONTENT="0; URL=' . $path . '">';    }    public function getEventi()    {        return $this->array_eventi;    }    public function getNomeGara($id_gara)    {        return $this->ranking->getNomeGara($id_gara);    }    public function getGare()    {        return $this->gare;    }}