<?phpif (!defined("_BASEDIR_"))    exit();include_model("Modello");class RankingErrorLog extends Modello{    private $conn = null;    public function __construct($id = NULL)    {        parent::__construct("log_errori_ranking", "id", $id);        $this->conn = $GLOBALS["connint"];        $this->conn->connetti();    }       public function errorLogRanking($descrizione,$nome_file, $nota = "")    {        $sql = "INSERT INTO log_errori_ranking  (descrizione,nome_file, nota) VALUES('$descrizione','$nome_file','$nota') ";        $res_insert = $this->conn->query($sql);        return $res_insert;    }}