<?phpif (!defined("_BASEDIR_"))    exit();include_model("Modello");class RankingLog extends Modello{    private $conn = null;    private $id_gara = NULL;    private $id_ranking_risultati = NULL;    private $id_utente = NULL;    private $descrizione = NULL;    private $classificato = NULL;    private $kategoria = NULL;    public function __construct($id = NULL)    {        parent::__construct("log_insertRanking", "id", $id);        $this->conn = $GLOBALS["connint"];        $this->conn->connetti();    }    public function setDati($dati = NULL)    {        $this->id_gara = $dati['id_gara'];        $this->id_ranking_risultati = $dati['id_ranking_risultati'];        $this->id_utente = $dati['id_utente'];        $this->descrizione = $dati['descrizione'];        $this->classificato = $dati['classificato'];        $this->kategoria = $dati['kategoria'];    }    public function insertLogRanking()    {        $sql = "INSERT INTO log_insertRanking  (id_gara,id_ranking_risultati, data_inserimento, id_utente,descrizione,classificato,kategoria ) VALUES($this->id_gara,$this->id_ranking_risultati, CURRENT_TIMESTAMP, $this->id_utente,'$this->descrizione', $this->classificato, $this->kategoria) ";        $res_insert = $this->conn->query($sql);        return $res_insert;    }}