<?phpif (!defined("_BASEDIR_"))    exit();require_once("../../config.inc.php");include_controller("admin/EliminagaraRanking");class EliminagaraRankingView{    private $ctrl;    private $fin;    public function __construct()    {        $this->ctrl = new EliminagaraRankingCtrl();    }    public function stampa()    {        $lang = Lingua::getParole();        ?>        <form enctype="multipart/form-data" method="POST">          <h4>Elimina gara dal Ranking: <select id="gare" name="gare" style="width: 252px;">              <?php              foreach ($this->ctrl->getEventi() as $idg => $g)              {                  $id_gara = $g['id_gara'];                  $ng = $this->ctrl->getNomeGara($g['id_gara']);                  $nome_gara = $ng['nome'];                  echo "<option value=$id_gara>$nome_gara</option>";              }              ?>            </select>          </h4>            <br><br><br>            <input name="processa" type="submit" value="<?php echo $lang["elimina_locandina"]; ?>">            </form>            <?php        }    }    