<?phpif (!defined("_BASEDIR_"))    exit();require_once("../config.inc.php");include_controller("org/convocazioni_stage");class ConvocazioniStageView{    private $ctrl;    private $arb;    private $arb_est;    private $arb_conv;    private $turni;    private $gara;    public function __construct($id_gara)    {        $this->ctrl = new ConvocazioniStageCtrl($id_gara);        $this->gara = new Gara($id_gara);    }    public function stampa()    {        $lang = Lingua::getParole();        //INFORMAZIONI GARA        $nome = $this->gara->getNome();        $data = $this->gara->getDataGara()->format('d/m/Y');        $rimborso = $this->gara->getRimborsoArb();        ?>        <br><form  accept-charset="UTF-8"  method="post" enctype="multipart/form-data">        <div id="Right" style="width:95%;">          <div class="Gare_soc_right"><h1 style="text-align: center;"><?php echo $lang["atleti"]; ?></h1></div>          <table class="atleti">            <th><div class='thAtleti '><?php echo $lang["cognome_iscrizioni"]; ?></div></th>            <th><div class='thAtleti'><?php echo $lang["nome_iscrizioni"]; ?></div></th>            <th><div class="thAtleti" ><?php echo $lang["sesso_iscrizioni"]; ?></div></th>            <th><div class='thAtleti'><?php echo $lang["nascita_iscrizioni"]; ?></div></th>            <th><div class='thAtleti'><?php echo $lang["societa"]; ?></div></th>            <th><div class="thAtleti" >Convalida</div></th>            <?php            $partecipanti_non_convalidati = $this->ctrl->getPartecipanti_non_convalidati();            $stage = new Stage(null, 'partecipanti_stage');            $pari = true;            for ($i = 0; $i < count($partecipanti_non_convalidati); $i++)            {                $partecipante = $partecipanti_non_convalidati[$i];                $id_atleta = $partecipante['id_atleta'];                $id_societa = $partecipante['id_societa'];                              $anagrafica_atleta = $stage->getAnagraficaAtleta($id_atleta);                if ($pari)                {                    print '<tr class="riga1">';                    $pari = false;                } else                {                    print '<tr class="riga2">';                    $pari = true;                }                print '<td  class="cognome">' . $anagrafica_atleta['cognome'] . "</td>";                print '<td class="nome">' . $anagrafica_atleta['nome'] . "</td>";                print '<td class="sesso">' . $anagrafica_atleta['sesso'] . "</td>";                print '<td class="nascita">' . $anagrafica_atleta['data_nascita'] . "</td>";                print '<td class="nascita">' . $anagrafica_atleta['nomebreve'] . "</td>";                                print '<td>'.'<input type="checkbox"  name="stage_nazionale['.$id_societa.'][]" value="'.$id_atleta.'"> </td>';                               print '</tr>';            }            ?>          </table></div>        <div class="pulsante tr" style="text-align:center">          <input type="submit" value="<?php echo $lang["salva_iscrizioni"]; ?>" />        </div></form>        <?php    }}